import Category from "../models/category";
import Meal from "../models/meal";
export var CATEGORIES = [new Category('c1', 'Biryani', '#f5428d'), new Category('c2', 'Karahi', '#f54242'), new Category('c3', 'Tikka', '#f5a442'), new Category('c4', 'Pakistani Bread', '#f5d142'), new Category('c5', 'Curry', '#368dff'), new Category('c6', 'Fast Food', '#41d95d'), new Category('c7', 'Pakistani Sweets', '#9eecff'), new Category('c8', 'Pakistani Drinks', '#b9ffb0')];
export var MEALS = [new Meal('m1', ['c1'], 'Chicken Biryani', 'affordable', 'challenging', 'https://www.example.com/chicken_biryani.jpg', 60, ['1 kg Chicken', '500g Basmati Rice', '2 Onions', '4 Tomatoes', '1 cup Yogurt', '2 tbsp Biryani Masala', '1 tsp Turmeric Powder', '1 tsp Red Chili Powder', '1 tbsp Ginger-Garlic Paste', '1 cup Fried Onions', '1/2 cup Mint Leaves', '1/2 cup Coriander Leaves', '4 cups Water', 'Salt to taste'], ['Marinate chicken with yogurt, ginger-garlic paste, red chili powder, and salt. Let it sit for 30 minutes.', 'In a separate pan, heat oil and fry onions until golden brown. Remove half of the fried onions and set aside for garnishing.', 'In the same pan, add tomatoes, biryani masala, turmeric powder, and cook until the tomatoes are soft.', 'Add marinated chicken to the pan and cook until the chicken is tender.', 'In a separate pot, boil water and cook basmati rice until it is 70% cooked. Drain the rice.', 'Layer the partially cooked rice over the chicken in the pan. Sprinkle fried onions, mint leaves, and coriander leaves on top.', 'Cover the pan and cook on low heat for 20-25 minutes until the rice is fully cooked and flavors are infused.', 'Serve hot with raita or salad.'], false, false, false, true), new Meal('m2', ['c2'], 'Chicken Karahi', 'affordable', 'simple', 'https://www.example.com/chicken_karahi.jpg', 30, ['1 kg Chicken', '4 Tomatoes', '3-4 Green Chilies', '2 Onions', '1 tbsp Ginger-Garlic Paste', '1 tbsp Red Chili Powder', '1 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1/2 cup Yogurt', '1/4 cup Fresh Coriander Leaves', 'Salt to taste', 'Oil for cooking'], ['Heat oil in a karahi or deep pan. Add sliced onions and cook until golden brown.', 'Add ginger-garlic paste and cook for a minute.', 'Add chicken pieces and cook until they change color.', 'Add chopped tomatoes, red chili powder, turmeric powder, cumin powder, and coriander powder. Cook until the tomatoes are soft.', 'Add yogurt and salt. Mix well and cook for 5-7 minutes until the oil separates.', 'Add green chilies and fresh coriander leaves. Cook for another 2-3 minutes.', 'Serve hot with naan or rice.'], false, false, false, false), new Meal('m3', ['c3'], 'Chicken Tikka', 'affordable', 'simple', 'https://www.example.com/chicken_tikka.jpg', 45, ['500g Chicken Pieces (boneless)', '1/2 cup Yogurt', '2 tbsp Lemon Juice', '2 tbsp Ginger-Garlic Paste', '2 tsp Red Chili Powder', '1 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', 'Salt to taste', '2 tbsp Oil', 'Skewers for grilling'], ['In a mixing bowl, combine yogurt, lemon juice, ginger-garlic paste, red chili powder, turmeric powder, cumin powder, coriander powder, garam masala, and salt to make a marinade.', 'Add chicken pieces to the marinade and mix well. Let it marinate for at least 2 hours or overnight in the refrigerator.', 'Preheat the grill or oven to medium-high heat.', 'Thread marinated chicken pieces onto skewers and brush them with oil.', 'Grill the chicken skewers for 10-15 minutes, turning occasionally, until the chicken is cooked and slightly charred.', 'Serve hot with mint chutney and naan.'], false, false, false, false), new Meal('m4', ['c4'], 'Naan', 'affordable', 'simple', 'https://www.example.com/naan.jpg', 120, ['3 cups All-Purpose Flour', '1 tsp Active Dry Yeast', '1 tsp Sugar', '1/2 cup Yogurt', '1/2 cup Milk', '1 tsp Baking Powder', '1/2 tsp Baking Soda', '2 tbsp Oil', 'Salt to taste', 'Butter for brushing'], ['In a small bowl, dissolve yeast and sugar in warm water. Let it sit for 5 minutes until it becomes frothy.', 'In a large mixing bowl, combine flour, baking powder, baking soda, and salt.', 'Add yogurt, milk, oil, and the yeast mixture to the dry ingredients. Mix well to form a soft dough.', 'Knead the dough on a lightly floured surface for 5-7 minutes until it is smooth and elastic.', 'Place the dough in a greased bowl, cover it with a damp cloth, and let it rise in a warm place for 1-2 hours until it doubles in size.', 'Preheat the oven to the highest temperature setting or heat a tandoor or skillet over medium-high heat.', 'Divide the dough into equal-sized balls. Roll out each ball into an oval or round shape.', 'Place the rolled naan on a baking sheet or hot skillet and cook for 2-3 minutes on each side until it puffs up and gets golden brown spots.', 'Brush the cooked naan with butter and serve hot with curry or kebabs.'], false, false, false, true), new Meal('m5', ['c5'], 'Chicken Curry', 'affordable', 'simple', 'https://www.example.com/chicken_curry.jpg', 60, ['500g Chicken Pieces (with bone)', '2 Onions (finely chopped)', '2 Tomatoes (finely chopped)', '2 Green Chilies (slit)', '3 tbsp Oil', '2 tsp Ginger-Garlic Paste', '1 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', 'Salt to taste', 'Fresh Coriander Leaves (for garnish)'], ['Heat oil in a large pan or pressure cooker.', 'Add chopped onions and sauté until they become golden brown.', 'Add ginger-garlic paste and sauté for another minute.', 'Add chopped tomatoes, red chili powder, turmeric powder, cumin powder, coriander powder, garam masala, and salt. Cook until the tomatoes are soft.', 'Add chicken pieces and mix well. Cook for 5 minutes until the chicken changes color.', 'If using a pressure cooker, add a little water, close the lid, and cook for 10-15 minutes or until the chicken is tender. If using a pan, add 1-2 cups of water, cover, and simmer for 30-40 minutes.', 'Garnish with fresh coriander leaves and serve hot with rice or naan.'], false, false, false, false), new Meal('m6', ['c3'], 'Seekh Kebab', 'affordable', 'challenging', 'https://www.example.com/seekh_kebab.jpg', 90, ['500g Minced Beef or Lamb', '1 Onion (finely chopped)', '2 Green Chilies (finely chopped)', '2 tbsp Ginger-Garlic Paste', '1 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', 'Salt to taste', '2 tbsp Gram Flour (Besan)', '2 tbsp Fresh Coriander Leaves (chopped)', '2 tbsp Oil', 'Skewers for grilling'], ['In a mixing bowl, combine minced meat, chopped onion, green chilies, ginger-garlic paste, red chili powder, turmeric powder, cumin powder, coriander powder, garam masala, salt, gram flour, and fresh coriander leaves.', 'Mix well using your hands or a spoon until all the ingredients are combined.', 'Cover the bowl and let the mixture marinate in the refrigerator for at least 1 hour.', 'Preheat the grill or oven to medium-high heat.', 'Take a small portion of the marinated mixture and shape it into a long sausage-like kebab around the skewer.', 'Repeat with the remaining mixture and skewers.', 'Brush the kebabs with oil and grill them for 10-15 minutes, turning occasionally, until they are cooked through and lightly charred.', 'Serve hot with mint chutney and naan.'], false, false, false, false), new Meal('m7', ['c1'], 'Vegetable Biryani', 'affordable', 'challenging', 'https://www.example.com/vegetable_biryani.jpg', 90, ['2 cups Basmati Rice', '1 cup Mixed Vegetables (carrots, peas, beans, etc.)', '1 Onion (thinly sliced)', '2 Tomatoes (chopped)', '2 Green Chilies (slit)', '1/4 cup Yogurt', '2 tsp Ginger-Garlic Paste', '1 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', 'A pinch of Saffron strands (soaked in milk)', '4 cups Water', '4 tbsp Ghee or Oil', 'Salt to taste', 'Fresh Coriander Leaves (for garnish)', 'Fried Onions (for garnish)', 'Mint Leaves (for garnish)'], ['Wash the rice in cold water and soak it for 30 minutes.', 'In a large pan, heat ghee or oil over medium heat.', 'Add sliced onions and sauté until they turn golden brown and crispy. Remove half of the fried onions for garnishing.', 'In the same pan, add ginger-garlic paste and sauté for a minute.', 'Add chopped tomatoes, green chilies, red chili powder, turmeric powder, cumin powder, coriander powder, garam masala, and salt. Cook until the tomatoes are soft.', 'Add mixed vegetables and yogurt. Mix well and cook for 5 minutes.', 'Drain the soaked rice and add it to the pan. Mix gently to combine.', 'Add water and saffron strands soaked in milk. Bring it to a boil, then reduce the heat, cover the pan, and let it cook for 10-15 minutes or until the rice is fully cooked and the flavors are infused.', 'Garnish with fried onions, fresh coriander leaves, and mint leaves. Serve hot with raita or salad.'], true, false, false, false), new Meal('m8', ['c5'], 'Haleem', 'luxurious', 'challenging', 'https://www.example.com/haleem.jpg', 240, ['1 cup Boneless Chicken or Beef (cooked and shredded)', '1 cup Broken Wheat (Dalia)', '1/4 cup Yellow Split Peas (Chana Dal)', '1/4 cup Split Red Lentils (Masoor Dal)', '1/4 cup Split Bengal Gram (Chana Dal)', '1 Onion (thinly sliced)', '2 Tomatoes (chopped)', '2 Green Chilies (slit)', '2 tbsp Ginger-Garlic Paste', '1 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', '4 cups Water or Chicken/Beef Stock', '4 tbsp Ghee or Oil', 'Salt to taste', 'Fresh Coriander Leaves (for garnish)', 'Fried Onions (for garnish)', 'Ginger Julienne (for garnish)', 'Lemon wedges (for serving)'], ['Wash broken wheat, yellow split peas, split red lentils, and split bengal gram. Soak them in water for 1 hour.', 'In a large pan or pressure cooker, heat ghee or oil over medium heat.', 'Add sliced onions and sauté until they turn golden brown and crispy. Remove half of the fried onions for garnishing.', 'In the same pan, add ginger-garlic paste and sauté for a minute.', 'Add chopped tomatoes, green chilies, red chili powder, turmeric powder, cumin powder, coriander powder, garam masala, and salt. Cook until the tomatoes are soft.', 'Add soaked broken wheat and lentils mixture, shredded chicken or beef, and water or stock. Mix well.', 'If using a pressure cooker, close the lid and cook for 30-40 minutes until the grains and lentils are completely soft and mashed. If using a pan, cover and simmer for 2-3 hours, stirring occasionally and adding more water if needed.', 'Once the haleem reaches the desired consistency, garnish with fried onions, fresh coriander leaves, and ginger julienne.', 'Serve hot with lemon wedges and naan.'], false, true, false, false), new Meal('m9', ['c5'], 'Nihari', 'luxurious', 'challenging', 'https://www.example.com/nihari.jpg', 360, ['500g Beef Shank or Lamb (with bone)', '2 Onions (thinly sliced)', '2 tbsp Ginger-Garlic Paste', '1/4 cup Wheat Flour (Atta)', '2 tbsp Nihari Masala Powder', '1 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', '1/2 cup Ghee or Oil', '4 cups Water', 'Salt to taste', 'Ginger Julienne (for garnish)', 'Fresh Coriander Leaves (for garnish)', 'Fried Onions (for garnish)', 'Lemon wedges (for serving)'], ['Heat ghee or oil in a large pan or pressure cooker over medium heat.', 'Add sliced onions and sauté until they turn golden brown.', 'Add ginger-garlic paste and sauté for a minute.', 'Add beef shank or lamb pieces and cook until they are browned.', 'In a separate bowl, mix wheat flour with some water to make a smooth paste (slurry).', 'Add nihari masala powder, red chili powder, turmeric powder, coriander powder, garam masala, and salt to the meat. Mix well.', 'Add the wheat flour slurry and water to the pan. Mix again.', 'If using a pressure cooker, close the lid and cook for 4-5 whistles or until the meat is tender. If using a pan, cover and simmer for 4-5 hours, stirring occasionally and adding more water if needed.', 'Garnish with ginger julienne, fresh coriander leaves, and fried onions.', 'Serve hot with naan or rice, and lemon wedges.'], false, true, false, false), new Meal('m10', ['c6'], 'Samosa', 'affordable', 'challenging', 'https://www.example.com/samosa.jpg', 60, ['For the dough:', '2 cups All-Purpose Flour', '1/2 tsp Carom Seeds (Ajwain)', '1/4 tsp Salt', '3 tbsp Oil', 'Water as needed', 'For the filling:', '2 Potatoes (boiled and mashed)', '1/2 cup Peas (boiled)', '1 Onion (finely chopped)', '2 Green Chilies (finely chopped)', '1 tsp Ginger-Garlic Paste', '1 tsp Cumin Seeds', '1 tsp Coriander Seeds', '1/2 tsp Red Chili Powder', '1/2 tsp Turmeric Powder', '1 tsp Garam Masala', 'Salt to taste', 'Oil for frying'], ['In a mixing bowl, combine all-purpose flour, carom seeds, salt, and oil. Mix well.', 'Gradually add water and knead to form a smooth and pliable dough.', 'Cover the dough with a damp cloth and let it rest for 30 minutes.', 'In a separate pan, heat oil over medium heat.', 'Add cumin seeds and coriander seeds. Sauté until they splutter.', 'Add chopped onions, green chilies, and ginger-garlic paste. Sauté until the onions become translucent.', 'Add boiled and mashed potatoes, boiled peas, red chili powder, turmeric powder, garam masala, and salt. Mix well and cook for 2-3 minutes.', 'Remove the pan from heat and let the filling cool down.', 'Divide the dough into small balls and roll each ball into a thin circle.', 'Cut each circle in half. Take one half and fold it into a cone shape, sealing the edges with water.', 'Fill the cone with the potato filling and seal the top edge with water to close the cone.', 'Repeat with the remaining dough and filling.', 'Heat oil for frying in a deep pan or kadhai over medium heat.', 'Deep fry the samosas until they turn golden brown and crispy.', 'Serve hot with tamarind chutney or mint chutney.'], false, false, false, true), new Meal('m11', ['c4'], 'Cheese Paratha', 'affordable', 'easy', 'https://www.example.com/cheese_paratha.jpg', 40, ['2 cups Whole Wheat Flour', '1/2 tsp Salt', 'Water as needed', '1 cup Grated Cheese (cheddar or mozzarella)', '1/2 tsp Red Chili Flakes', '1/2 tsp Dried Oregano', '1/2 tsp Dried Basil', 'Salt to taste', 'Oil or ghee for frying'], ['In a mixing bowl, combine whole wheat flour and salt. Gradually add water and knead to form a soft dough.', 'Cover the dough and let it rest for 15-20 minutes.', 'In a separate bowl, mix grated cheese, red chili flakes, dried oregano, dried basil, and salt.', 'Divide the dough into small balls and roll each ball into a small circle.', 'Place a portion of the cheese mixture in the center of the circle.', 'Bring the edges of the dough together and seal it to form a ball.', 'Flatten the ball and roll it gently into a paratha.', 'Heat a tawa or griddle and cook the paratha on both sides until golden brown, applying oil or ghee as needed.', 'Serve hot with raita or ketchup.'], false, false, false, true), new Meal('m12', ['c5'], 'Aloo Gobi', 'affordable', 'easy', 'https://www.example.com/aloo_gobi.jpg', 40, ['2 cups Cauliflower Florets', '2 Potatoes (peeled and diced)', '1 Onion (finely chopped)', '2 Tomatoes (finely chopped)', '2 Green Chilies (slit lengthwise)', '1 tbsp Ginger-Garlic Paste', '1/2 tsp Turmeric Powder', '1 tsp Red Chili Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1/2 tsp Garam Masala', 'Salt to taste', 'Oil or ghee for cooking', 'Fresh coriander leaves for garnishing'], ['Heat oil or ghee in a pan over medium heat.', 'Add chopped onions and sauté until they turn golden brown.', 'Add ginger-garlic paste and sauté for a minute until fragrant.', 'Add chopped tomatoes and cook until they turn soft.', 'Add turmeric powder, red chili powder, cumin powder, coriander powder, and salt. Mix well.', 'Add diced potatoes and cauliflower florets. Mix until the masala coats the vegetables.', 'Cover the pan and cook on low heat until the potatoes and cauliflower are tender (about 15-20 minutes).', 'Sprinkle garam masala and adjust the seasoning if needed.', 'Garnish with fresh coriander leaves.', 'Serve hot with roti or rice.'], false, false, true, false), new Meal('m13', ['c5'], 'Palak Ghost', 'expensive', 'moderate', 'https://www.example.com/palak_ghost.jpg', 90, ['500g Mutton (preferably with bones)', '2 cups Spinach (palak)', '2 Onions (finely chopped)', '2 Tomatoes (finely chopped)', '2 Green Chilies (slit lengthwise)', '1 tbsp Ginger-Garlic Paste', '1/2 tsp Turmeric Powder', '1 tsp Red Chili Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1 tsp Garam Masala', 'Salt to taste', 'Oil or ghee for cooking'], ['In a pressure cooker, heat oil or ghee over medium heat.', 'Add chopped onions and sauté until they turn golden brown.', 'Add ginger-garlic paste and sauté for a minute until fragrant.', 'Add chopped tomatoes and cook until they turn soft.', 'Add mutton pieces and sauté for a few minutes until they are browned.', 'Add turmeric powder, red chili powder, cumin powder, coriander powder, and salt. Mix well.', 'Add water as needed to cover the mutton.', 'Close the pressure cooker and cook the mutton until tender (about 4-5 whistles).', 'Once the pressure is released, open the cooker and add spinach (palak).', 'Cook for a few minutes until the spinach wilts and blends well with the mutton.', 'Sprinkle garam masala and adjust the seasoning if needed.', 'Simmer for a few more minutes.', 'Serve hot with rice or roti.'], false, false, true, false), new Meal('m13', ['c5'], 'Aloo Gobi', 'affordable', 'easy', 'https://www.example.com/aloo_gobi.jpg', 40, ['2 cups Cauliflower Florets', '2 Potatoes (peeled and diced)', '1 Onion (finely chopped)', '2 Tomatoes (finely chopped)', '2 Green Chilies (slit lengthwise)', '1 tbsp Ginger-Garlic Paste', '1/2 tsp Turmeric Powder', '1 tsp Red Chili Powder', '1 tsp Cumin Powder', '1 tsp Coriander Powder', '1/2 tsp Garam Masala', 'Salt to taste', 'Oil or ghee for cooking', 'Fresh coriander leaves for garnishing'], ['Heat oil or ghee in a pan over medium heat.', 'Add chopped onions and sauté until they turn golden brown.', 'Add ginger-garlic paste and sauté for a minute until fragrant.', 'Add chopped tomatoes and cook until they turn soft.', 'Add turmeric powder, red chili powder, cumin powder, coriander powder, and salt. Mix well.', 'Add diced potatoes and cauliflower florets. Mix until the masala coats the vegetables.', 'Cover the pan and cook on low heat until the potatoes and cauliflower are tender (about 15-20 minutes).', 'Sprinkle garam masala and adjust the seasoning if needed.', 'Garnish with fresh coriander leaves.', 'Serve hot with roti or rice.'], false, false, true, false), new Meal('m14', ['c4'], 'Cheese Paratha', 'affordable', 'easy', 'https://www.example.com/cheese_paratha.jpg', 40, ['2 cups Whole Wheat Flour', '1/2 tsp Salt', 'Water as needed', '1 cup Grated Cheese (cheddar or mozzarella)', '1/2 tsp Red Chili Flakes', '1/2 tsp Dried Oregano', '1/2 tsp Dried Basil', 'Salt to taste', 'Oil or ghee for frying'], ['In a mixing bowl, combine whole wheat flour and salt. Gradually add water and knead to form a soft dough.', 'Cover the dough and let it rest for 15-20 minutes.', 'In a separate bowl, mix grated cheese, red chili flakes, dried oregano, dried basil, and salt.', 'Divide the dough into small balls and roll each ball into a small circle.', 'Place a portion of the cheese mixture in the center of the circle.', 'Bring the edges of the dough together and seal it to form a ball.', 'Flatten the ball and roll it gently into a paratha.', 'Heat a tawa or griddle and cook the paratha on both sides until golden brown, applying oil or ghee as needed.', 'Serve hot with raita or ketchup.'], false, false, false, true), new Meal('m15', ['c7'], 'Kheer', 'affordable', 'easy', 'https://www.example.com/kheer.jpg', 60, ['1/4 cup Rice', '4 cups Milk', '1/2 cup Sugar', '1/4 cup Mixed Nuts (cashews, almonds, pistachios), chopped', '1/2 tsp Cardamom Powder', 'Saffron strands for garnishing', 'Rose petals for garnishing'], ['Wash the rice thoroughly and soak it in water for 30 minutes.', 'In a large saucepan, bring the milk to a boil.', 'Drain the soaked rice and add it to the boiling milk.', 'Cook on low heat, stirring occasionally, until the rice is fully cooked and the milk thickens (approximately 30-40 minutes).', 'Add sugar and mix well until it dissolves.', 'Add chopped nuts and cardamom powder. Mix well.', 'Remove from heat and let it cool.', 'Garnish with saffron strands and rose petals.', 'Refrigerate for a few hours before serving.', 'Serve chilled.'], false, true, false, false), new Meal('m16', ['c7'], 'Custard', 'affordable', 'easy', 'https://www.example.com/custard.jpg', 30, ['2 cups Milk', '3 tbsp Custard Powder', '2-3 tbsp Sugar (adjust to taste)', 'Assorted fruits (e.g., bananas, apples, grapes, strawberries), chopped', 'Nuts for garnishing (e.g., almonds, cashews), chopped'], ['In a saucepan, heat milk over medium heat until it comes to a gentle boil.', 'In a separate bowl, mix custard powder with a little milk to form a smooth paste.', 'Add the custard paste to the boiling milk, stirring continuously.', 'Add sugar and continue stirring until the custard thickens.', 'Remove from heat and let it cool.', 'In serving bowls or glasses, layer the chopped fruits.', 'Pour the custard over the fruits.', 'Garnish with chopped nuts.', 'Refrigerate for a few hours to chill.', 'Serve cold.'], false, true, false, false), new Meal('m17', ['c7'], 'Halwa', 'affordable', 'moderate', 'https://www.example.com/halwa.jpg', 45, ['1 cup Semolina (Sooji/Rava)', '1/2 cup Ghee', '1 cup Sugar', '2 cups Water', '1/4 cup Mixed Nuts (cashews, almonds, raisins), chopped', '1/2 tsp Cardamom Powder', 'Saffron strands for garnishing'], ['In a pan, heat ghee over medium heat.', 'Add semolina and roast it until it turns golden brown and aromatic.', 'In a separate saucepan, heat water and sugar, and bring it to a boil to make a sugar syrup.', 'Add the sugar syrup to the roasted semolina slowly, stirring continuously to avoid lumps.', 'Cook on low heat, stirring continuously until the halwa thickens and starts to leave the sides of the pan.', 'Add chopped nuts and cardamom powder. Mix well.', 'Remove from heat.', 'Garnish with saffron strands.', 'Serve hot or warm.'], false, false, false, false), new Meal('m18', ['c7'], 'Sawiyan', 'affordable', 'easy', 'https://www.example.com/sawiyan.jpg', 30, ['1 cup Vermicelli (Sawiyan)', '2 cups Milk', '1/4 cup Sugar (adjust to taste)', '1/4 cup Mixed Nuts (cashews, almonds, pistachios), chopped', '1/2 tsp Cardamom Powder', 'Saffron strands for garnishing'], ['In a pan, heat ghee or oil over medium heat.', 'Add vermicelli and roast until it turns golden brown.', 'In a separate saucepan, heat milk and bring it to a boil.', 'Add the roasted vermicelli to the boiling milk.', 'Cook on low heat, stirring occasionally, until the vermicelli is fully cooked and the milk thickens.', 'Add sugar and mix well until it dissolves.', 'Add chopped nuts and cardamom powder. Mix well.', 'Remove from heat.', 'Garnish with saffron strands.', 'Serve hot or cold, as desired.'], false, true, false, false), new Meal('m19', ['c5'], 'Aloo ki Bhujia', 'affordable', 'easy', 'https://www.example.com/aloo_bhujia.jpg', 30, ['4 Potatoes (medium-sized, peeled and thinly sliced)', '2 tbsp Oil', '1 tsp Cumin Seeds', '1 Onion (finely chopped)', '2 Green Chilies (finely chopped)', '1/2 tsp Turmeric Powder', '1/2 tsp Red Chili Powder', 'Salt to taste', 'Fresh Coriander Leaves (for garnishing)'], ['Heat oil in a pan over medium heat.', 'Add cumin seeds and let them splutter.', 'Add chopped onions and green chilies. Sauté until the onions become translucent.', 'Add the thinly sliced potatoes and mix well.', 'Add turmeric powder, red chili powder, and salt. Mix well to coat the potatoes evenly.', 'Cover the pan and cook on low heat until the potatoes are tender and cooked through.', 'Stir occasionally to prevent sticking or burning.', 'Garnish with fresh coriander leaves.', 'Serve hot with roti or puri.'], false, false, false, true), new Meal('m20', ['c5'], 'Lahori Paye', 'expensive', 'challenging', 'https://www.example.com/lahori_paye.jpg', 240, ['4 Lamb Paye (trotters)', '1 cup Wheat Flour', '2 Onions (finely chopped)', '2 Tomatoes (finely chopped)', '1 tbsp Ginger-Garlic Paste', '2 tbsp Ghee', '1 tbsp Red Chili Powder', '1 tsp Turmeric Powder', '1 tbsp Coriander Powder', '1 tbsp Garam Masala', '1/4 cup Fresh Coriander Leaves (chopped)', 'Salt to taste'], ['Wash the lamb trotters thoroughly and soak them in water for 1 hour.', 'In a pressure cooker, add the soaked trotters, water, and salt.', 'Pressure cook for about 45-60 minutes until the trotters are tender and the meat falls off the bones.', 'In a separate pan, heat ghee over medium heat.', 'Add chopped onions and sauté until golden brown.', 'Add ginger-garlic paste and sauté for a minute.', 'Add chopped tomatoes and cook until they turn soft.', 'Add red chili powder, turmeric powder, coriander powder, and salt. Mix well.', 'Cook the masala until the ghee separates from it.', 'Add the cooked trotters along with the stock to the masala. Mix well.', 'Cover the pan and simmer for about 2 hours on low heat.', 'Meanwhile, in a separate pan, roast wheat flour until it turns golden brown and aromatic. Keep stirring to prevent burning.', 'Add the roasted wheat flour to the simmering trotters and mix well.', 'Continue to cook until the paye reach a thick and smooth consistency.', 'Add garam masala and fresh coriander leaves. Mix well.', 'Serve hot with naan or rice.'], true, false, true, false), new Meal('m21', ['c2'], 'Dam Phookt', 'affordable', 'easy', 'https://www.example.com/dam_phookt.jpg', 20, ['2 cups Puffed Rice (murmura)', '1 Onion (finely chopped)', '1 Tomato (finely chopped)', '1 Green Chili (finely chopped)', '1/4 cup Coriander Leaves (chopped)', '1/2 Lemon (juiced)', '1 tsp Chaat Masala', 'Salt to taste'], ['In a large mixing bowl, add puffed rice.', 'Add chopped onions, tomatoes, green chili, and coriander leaves.', 'Squeeze lemon juice over the mixture.', 'Sprinkle chaat masala and salt.', 'Mix everything well.', 'Serve immediately as a light and crunchy snack.'], false, false, false, false), new Meal('m22', ['c8'], 'Lassi', 'affordable', 'easy', 'https://www.example.com/lassi.jpg', 10, ['1 cup Yogurt', '1 cup Chilled Water', '4 tbsp Sugar (adjust to taste)', '1/2 tsp Cardamom Powder', 'Saffron strands (optional)', 'Crushed Ice (for serving)'], ['In a blender, add yogurt, chilled water, sugar, and cardamom powder.', 'Blend until smooth and frothy.', 'If desired, add a few saffron strands for extra flavor and color.', 'Pour the lassi into glasses filled with crushed ice.', 'Serve chilled.'], false, true, false, false), new Meal('m23', ['c8'], 'Rabri Doodh', 'expensive', 'moderate', 'https://www.example.com/rabri_doodh.jpg', 180, ['1 liter Milk', '1/2 cup Sugar (adjust to taste)', '1/4 cup Almonds (blanched and chopped)', '1/4 cup Pistachios (blanched and chopped)', '1/4 cup Cashews (chopped)', '1/4 cup Raisins', '1/2 tsp Cardamom Powder', 'Saffron strands for garnishing'], ['In a large heavy-bottomed pan, heat milk over medium heat.', 'Bring the milk to a boil, stirring occasionally to prevent burning.', 'Reduce the heat to low and simmer the milk, stirring occasionally, until it thickens to about half of its original quantity.', 'Add sugar, chopped nuts, raisins, and cardamom powder to the milk.', 'Continue to simmer and stir for another 15-20 minutes until the rabri thickens further and the flavors meld together.', 'Remove from heat and let it cool to room temperature.', 'Refrigerate the rabri doodh for a few hours until chilled.', 'Garnish with saffron strands before serving.', 'Serve chilled.'], true, false, false, false), new Meal('m24', ['c8'], 'Doodh Soda', 'affordable', 'easy', 'https://www.example.com/doodh_soda.jpg', 5, ['1 cup Milk', '1/2 cup Club Soda', '2 tbsp Sugar (adjust to taste)', '1/4 tsp Cardamom Powder', 'Ice Cubes (optional)'], ['In a glass, add milk.', 'Add club soda and stir gently to mix.', 'Add sugar and cardamom powder. Stir until the sugar dissolves.', 'If desired, add ice cubes for a chilled drink.', 'Serve immediately.'], false, false, false, false)];
